function validarEmail(email) {
  if (!email) return false;

  email = email.toString().trim().toLowerCase();

  // regex segura (nem permissiva demais, nem chata demais)
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/;

  return regex.test(email);
}

